create table if not exists wallet (id bigint generated by default as identity, amount decimal(19, 2), currency varchar(255), primary key (id));
create table if not exists exchange_rate (from_currency varchar(255) not null, to_currency varchar(255) not null, rate decimal(19,10), timestamp bigint not null, primary key (from_currency, timestamp, to_currency));
create table if not exists commission (currency_from varchar(255) not null, currency_to varchar(255) not null, commission double, primary key (currency_from, currency_to));